mapper.insert:
insert into t_media
set iuser = #{iuser}
, title = #{title}
, date = #{date}
, genrepk = #{genrepk}
, is_saw = #{isSaw}
, del = 0

    , comment = null
    , star = null



        , comment = #{comment}


        , star = #{star}

mapper.update:
UPDATE t_media
SET

    title = #{title}


    , date = #{date}


    , genrepk = #{genrepk}


    , is_saw = #{isSaw}


    , comment = null
    , star = null



    , comment = #{comment}


    , star = #{star}


where iuser = #{iuser}
and imedia = #{imedia}
and del = 0

update t_media
set del = 1
where imedia = #{imedia}
and iuser = #{iuser}
and del = 0

mapper.select:
select p.pic, Day(m.date) as day,
COUNT(distinct m.imedia) as imedia
from t_media m
join t_media_pics p
ON m.imedia = p.imedia
WHERE MONTH (m.date) = #{month}
AND YEAR (m.date) = #{year}
and m.iuser = #{iuser}
and m.del = 0
GROUP BY m.date

select m.imedia, m.title, m.date, p.pic,
if(m.is_saw = 1, m.star, NULL) as star,
if(m.is_saw = 1, m.comment, NULL) as comment
from t_media m
join t_media_pics p
ON m.imedia = p.imedia
where m.is_saw = #{isSaw}
and m.iuser = #{iuser}
and m.del = 0
GROUP BY m.imedia

SELECT imedia,title, date, is_saw AS isSaw,
if(m.is_saw = 1, m.star, NULL) as star,
if(m.is_saw = 1, m.comment, NULL) as comment, genrepk
from t_media
WHERE iuser = #{iuser}
AND imedia = #{imedia}
and del = 0
